chrome.runtime.onInstalled.addListener(()=>{console.log("Notion2WeChat extension installed")});chrome.runtime.onMessage.addListener((e,n,o)=>{if(e.action==="convertContent")return t(e.data).then(o),!0});async function t(e){try{return await(await fetch("https://api.example.com/convert",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)})).json()}catch(n){return console.error("Conversion failed:",n),{error:"Conversion failed"}}}
